mapper.select:
SELECT COUNT(*)
FROM dbo.Account
WHERE loginId = #{loginId}

mapper.update:
UPDATE dbo.Account
SET loginId = #{loginId}
	,commission=#{commission}
	,telephone=#{telephone}
	,accountText=#{accountText}
	,recommenderAccountId=#{recommenderAccountId}
	,loginPassword=PwdEncrypt(#{loginPassword})
WHERE accountId = #{accountId}

UPDATE dbo.Account
SET accountState = #{accountState}
WHERE accountId = #{accountId}

UPDATE dbo.Account
SET gold = #{gold}
WHERE accountId = ${accountId}

UPDATE dbo.Account
SET ticket = #{ticket}
WHERE accountId = ${accountId}

mapper.select:
SELECT *
FROM dbo.Account
WHERE grade = #{grade}
AND recommenderAccountId=#{recommenderAccountId}
